import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
var timeAndDate = () => {
  const date = new Date();
  const value_ = `${date.getHours()}:${date.getMinutes()}:${date.getSeconds()} || ${date.getDate()}/${date.getMonth()}/${date.getFullYear()}`;
  return value_;
};
const nameInput = document.querySelector("#taskName");
const dateInput = document.querySelector("#date");
const timeInput = document.querySelector("#time");
const addBtn = document.querySelector("#addBtn");
addBtn.addEventListener("click", appendToArr, "false");
// var taskName = nameInput.value;
// console.log(taskName);
var taskArr = [
  {
    taskName: "Task_001",
    taskTime: timeAndDate(),
  },
  {
    taskName: "Task_002",
    taskTime: timeAndDate(),
  },
  {
    taskName: "Task_003",
    taskTime: timeAndDate(),
  },
  {
    taskName: "Task_004",
    taskTime: timeAndDate(),
  },
];

function Output() {
  return (
    <tasks className="tasks">
      <Input />
      {taskArr.map((task) => {
        return <Events data={task} />;
      })}
    </tasks>
  );
}
function Input() {
  return (
    <>
      <input type="text" placeHolder="Enter task name here" id="taskName" />
      <input type="date" id="date" />
      <input type="time" id="time" />
      <button id="addBtn">ADD</button>
    </>
  );
}
function Events(props) {
  var { taskName, taskTime } = props.data;
  return (
    <task>
      <taskName>{taskName}</taskName>
      <taskTime>{taskTime}</taskTime>
      <button>DELETE</button>
    </task>
  );
}
ReactDOM.render(<Output />, document.getElementById("root"));
