import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
const productArr = [
  {
    img: "https://m.media-amazon.com/images/I/71geVdy6-OS._AC_UL480_QL65_.jpg",
    name1: "OPPO A74 5G",
    price: "₹17,990",
    varient: "(Fantastic Purple,6GB RAM,128GB Storage)",
    features:
      "5G Android Smartphone | 5000 mAh Battery | 18W Fast Charge | 90Hz LCD Display",
  },
  {
    img: "https://m.media-amazon.com/images/I/61TnX0PmqES._AC_UL480_QL65_.jpg",
    name1: "OnePlus Nord 2 5G ",
    price: "₹29,999",
    varient: "(Blue Haze, 8GB RAM, 128GB Storage)",
    features:
      "5G Android Smartphone | 4500 mAh Battery | 30W Fast Charge | 90Hz Fluid AMOLED Display",
  },
];
function Output() {
  return (
    <div>
      {productArr.map((product) => {
        return (
          <Product
            img={product.img}
            name1={product.name1}
            price={product.price}
            varient={product.varient}
            features={product.features}
          />
        );
      })}
    </div>
  );
}

function Product(props) {
  const { img, name1, price, varient, features } = props;
  return (
    <div className="product">
      <img src={img} className="productImg" />
      <name>{name1}</name>
      <price>{price}</price>
      <varient>{varient}</varient>
      <features>{features}</features>
    </div>
  );
}
ReactDOM.render(<Output />, document.getElementById("root"));
