import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
var timeAndDate = () => {
  const date = new Date();
  const value_ = `${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}   ${date.getDate()}/${date.getMonth()}/${date.getFullYear()}`;
  return value_;
};

function Output() {
  var taskName = "",
    taskDate = "",
    taskTime = "";
  var taskArr1 = [
    {
      taskName: "Task_001",
      taskTime: timeAndDate(),
    },
    {
      taskName: "Task_002",
      taskTime: timeAndDate(),
    },
    {
      taskName: "Task_003",
      taskTime: timeAndDate(),
    },
    {
      taskName: "Task_004",
      taskTime: timeAndDate(),
    },
  ];
  const [taskArr, setTaskArr] = useState(taskArr1);
  var appendToArr = (task) => {
    const newTask = {
      taskName: task.taskName,
      taskTime: `${task.taskTime}||${task.taskDate}`,
    };
    setTaskArr(taskArr.push(newTask));
  };
  useState(taskArr.push());
  return (
    <div className="tasks">
      <>
        <input
          type="text"
          placeholder="Enter task name here"
          onChange={(val) => {
            taskName = val.target.value;
          }}
        />
        <input
          type="date"
          id="date"
          onChange={(val) => {
            taskDate = val.target.value;
          }}
        />
        <input
          type="time"
          id="time"
          onChange={(val) => {
            taskTime = val.target.value;
          }}
        />
        <button
          type="button"
          onClick={() => appendToArr({ taskName, taskDate, taskTime })}
        >
          ADD
        </button>
      </>
      {taskArr.map((task) => {
        return <Events data={task} />;
      })}
    </div>
  );
}

function Events(props) {
  var { taskName, taskTime } = props.data;
  return (
    <div className="task">
      <div>{taskName}</div>
      <div>{taskTime}</div>
      <button /*onClick={() => appendToArr1()}*/>DELETE</button>
    </div>
  );
}
ReactDOM.render(<Output />, document.getElementById("root"));
