import React, { useState } from "react";
import ReactDOM from "react-dom";
import "./index.css";
var timeAndDate = () => {
  const date = new Date();
  const value_ = `${date.getHours()}:${date.getMinutes()}:${date.getSeconds()}   ${date.getDate()}/${date.getMonth()}/${date.getFullYear()}`;
  return value_;
};
var sampleArr = [
  {
    taskName: "Task_001",
    taskTime: timeAndDate(),
  },
  {
    taskName: "Task_002",
    taskTime: timeAndDate(),
  },
  {
    taskName: "Task_003",
    taskTime: timeAndDate(),
  },
  {
    taskName: "Task_004",
    taskTime: timeAndDate(),
  },
];
function Output() {
  var taskName = "",
    taskDate = "",
    taskTime = "";
  const [taskArr, setTaskArr] = useState(sampleArr);
  var appendToArr = (taskObj) => {
    const newTask = {
      taskName: taskObj.taskName,
      taskTime: `${taskObj.taskTime}||${taskObj.taskDate}`,
    };
    setTaskArr((arr) => [...arr, newTask]);
  };
  return (
    <div className="tasks">
      <input
        type="text"
        placeholder="Enter task name here"
        onChange={(val) => {
          taskName = val.target.value;
        }}
      />
      <div class="input-group flex-nowrap">
        <span class="input-group-text" id="addon-wrapping">
          @
        </span>
        <input
          type="text"
          class="form-control"
          placeholder="Username"
          aria-label="Username"
          aria-describedby="addon-wrapping"
        />
      </div>
      <input
        type="date"
        id="date"
        onChange={(val) => {
          taskDate = val.target.value;
        }}
      />
      <input
        type="time"
        id="time"
        onChange={(val) => {
          taskTime = val.target.value;
        }}
      />
      <button
        type="button"
        class="btn btn-secondary"
        onClick={() => appendToArr({ taskName, taskDate, taskTime })}
      >
        ADD
      </button>

      <>
        {taskArr.map((taskObj1) => {
          return <Events data={taskObj1} />;
        })}
      </>
    </div>
  );
}

function Events(props) {
  var { taskName, taskTime } = props.data;
  return (
    <div className="task">
      <div>{taskName}</div>
      <div>{taskTime}</div>
      <button /*onClick={() => appendToArr1()}*/>DELETE</button>
    </div>
  );
}
ReactDOM.render(<Output />, document.getElementById("root"));
